mapper.select:
SELECT COUNT(*) FROM users
WHERE user_name = #{userName}
AND contact = #{contact}

SELECT * FROM users
WHERE user_name = #{userName}
AND contact = #{contact}

SELECT COUNT(*) > 0 FROM users
WHERE user_name = #{userName}
AND user_id = #{userId}

mapper.update:
UPDATE users
SET login_lock = #{loginLock}
WHERE user_id = #{userId}

mapper.select:
SELECT user_id FROM users
WHERE user_name = #{userName}
AND contact = #{contact}

mapper.update:
UPDATE users
SET user_pw = #{userPw}, mod_date = #{modDate}, login_lock = #{loginLock}
WHERE email = #{email}
